export function mochaAnalytics(env) {
    return {
        name: 'mocha-analytics',
        enforce: 'post',
        apply: 'build',
        transformIndexHtml(html) {
            const attrs = JSON.parse(env.ANALYTICS_SCRIPT_ATTRS ?? '{}');
            return {
                html,
                tags: [
                    {
                        tag: 'script',
                        attrs: { defer: true, ...attrs },
                        injectTo: 'head',
                    },
                ],
            };
        },
    };
}
