export function mochaTracker(config) {
    return {
        name: 'mocha-tracker',
        enforce: 'post',
        apply: 'build',
        transformIndexHtml(html) {
            if (!config.enabled) {
                return html;
            }
            return {
                html,
                tags: [
                    {
                        tag: 'script',
                        attrs: {
                            defer: true,
                            'data-domain': config.domain,
                            src: config.src,
                        },
                        injectTo: 'head',
                    },
                ],
            };
        },
    };
}
